import React, { useEffect, useState } from 'react';
import './App.css';

const ARC_LOCATIONS = [
  { from: 1, to: 3, location: 'East Blue' },
  { from: 4, to: 8, location: "Ville d'Orange" },
  { from: 9, to: 18, location: 'Village de Sirop' },
  { from: 19, to: 30, location: 'Baratie' },
  { from: 31, to: 45, location: 'Arlong Park' },
  { from: 48, to: 53, location: 'Loguetown' },
  { from: 61, to: 63, location: 'Reverse Mountain' },
  { from: 64, to: 67, location: 'Whiskey Peak' },
  { from: 70, to: 78, location: 'Little Garden' },
  { from: 79, to: 91, location: 'Royaume de Drum' },
  { from: 92, to: 130, location: 'Alabasta' },
  { from: 153, to: 195, location: 'Skypiea' },
  { from: 229, to: 263, location: 'Water 7' },
  { from: 264, to: 312, location: 'Enies Lobby' },
  { from: 337, to: 381, location: 'Thriller Bark' },
  { from: 385, to: 407, location: 'Archipel Sabaody' },
  { from: 408, to: 417, location: 'Amazon Lily' },
  { from: 422, to: 452, location: 'Impel Down' },
  { from: 457, to: 489, location: 'Marineford' },
  { from: 523, to: 574, location: 'Île des Hommes-Poissons' },
  { from: 579, to: 625, location: 'Punk Hazard' },
  { from: 629, to: 746, location: 'Dressrosa' },
  { from: 751, to: 779, location: 'Zou' },
  { from: 783, to: 877, location: 'Whole Cake Island' },
  { from: 890, to: 1088, location: 'Wano' },
];

function App() {
  const [image, setImage] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [options, setOptions] = useState([]);
  const [result, setResult] = useState('');
  const [dataset, setDataset] = useState([]);
  const [timer, setTimer] = useState(10);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    fetch("/onepiece_dataset.json")
      .then((res) => res.json())
      .then((data) => {
        setDataset(data);
        pickRandomImage(data);
      });
  }, []);

  useEffect(() => {
    if (timer === 0) {
      setResult('⏱️ Time is up! You lost! Starting a new game...');
      setGameOver(true);
      setTimeout(() => {
        setGameOver(false);
        pickRandomImage(dataset);
      }, 2500);
      return;
    }
    if (!gameOver) {
      const interval = setInterval(() => {
        setTimer(prev => Math.max(prev - 1, 0));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [timer, gameOver]);

  const getLocationByChapter = (chapter) => {
    const arc = ARC_LOCATIONS.find(a => chapter >= a.from && chapter <= a.to);
    return arc ? arc.location : 'Lieu inconnu';
  };

  const pickRandomImage = (data) => {
    const randomEntry = data[Math.floor(Math.random() * data.length)];
    const image = randomEntry.images[Math.floor(Math.random() * randomEntry.images.length)]
      .replace("https://lelscans.nethttps://lelscans.net", "https://lelscans.net");
    const chapter = randomEntry.chapter;

    const location = getLocationByChapter(chapter);
    setCorrectAnswer(location);
    setImage(image);

    const filteredArcs = ARC_LOCATIONS.filter(loc => loc.from >= 640 && loc.location !== location);
    const shuffled = [
      location,
      ...filteredArcs
        .sort(() => 0.5 - Math.random())
        .slice(0, 4) // Now we have 4 choices
        .map(loc => loc.location),
    ].sort(() => 0.5 - Math.random());

    setOptions(shuffled);
    setResult('');
    setTimer(10);  // Reset timer
  };

  const handleAnswer = (choice) => {
    if (choice === correctAnswer) {
      setResult('Nice job 🗿🗿');
    } else {
      setResult(`THIS IS NOT THIS ARC DUMB ASS 🫵🤣🤣... La bonne réponse était ${correctAnswer}.`);
    }
    setTimeout(() => pickRandomImage(dataset), 2500);
  };

  return (
    <div className="min-h-screen bg-black text-white p-6 text-center flex flex-col items-center">
      <div className="flex justify-center mb-4">
        <img
          src="/Luffy.jpg"
          alt="Luffy"
          className="w-40 h-40 object-cover rounded-full border-4"
          style={{ borderColor: '#CC213B' }}
        />
      </div>
      <h1 className="text-4xl font-bold mb-4" style={{ color: '#E6B96F' }}>
        OneGuessr: Grand Line Edition
      </h1>

      {/* Timer section */}
      <div className="relative mb-4 w-full max-w-[500px] mx-auto">
        <div
          className="h-2 bg-[#E6B96F] rounded-full"
          style={{ width: `${(timer / 10) * 100}%`, transition: 'width 1s linear' }}
        />
        <p className="absolute top-0 left-0 right-0 bottom-0 flex justify-center items-center text-lg font-bold">
          {timer} s
        </p>
      </div>

      {gameOver ? (
        <div className="mt-6 text-lg" style={{ color: '#E6B96F' }}>
          {result}
        </div>
      ) : (
        <div>
          <img
            src={image}
            alt="One Piece scene"
            className="mx-auto mb-4 rounded-lg shadow-lg max-h-[400px] border-4"
            style={{ borderColor: '#CC213B' }}
          />
          <div className="flex flex-wrap justify-center gap-4">
            {options.map((opt, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(opt)}
                className="text-black font-bold py-2 px-4 rounded-lg shadow"
                style={{ backgroundColor: '#E6B96F' }}
              >
                {opt}
              </button>
            ))}
          </div>
          <div className="mt-6 text-lg" style={{ color: '#E6B96F' }}>{result}</div>
        </div>
      )}
    </div>
  );
}

export default App;
