import React, { useEffect, useState } from 'react';
import './App.css';

const ARC_LOCATIONS = [
  { from: 1, to: 3, location: 'East Blue' },
  { from: 4, to: 8, location: "Ville d'Orange" },
  { from: 9, to: 18, location: 'Village de Sirop' },
  { from: 19, to: 30, location: 'Baratie' },
  { from: 31, to: 45, location: 'Arlong Park' },
  { from: 48, to: 53, location: 'Loguetown' },
  { from: 61, to: 63, location: 'Reverse Mountain' },
  { from: 64, to: 67, location: 'Whiskey Peak' },
  { from: 70, to: 78, location: 'Little Garden' },
  { from: 79, to: 91, location: 'Royaume de Drum' },
  { from: 92, to: 130, location: 'Alabasta' },
  { from: 153, to: 195, location: 'Skypiea' },
  { from: 229, to: 263, location: 'Water 7' },
  { from: 264, to: 312, location: 'Enies Lobby' },
  { from: 337, to: 381, location: 'Thriller Bark' },
  { from: 385, to: 407, location: 'Archipel Sabaody' },
  { from: 408, to: 417, location: 'Amazon Lily' },
  { from: 422, to: 452, location: 'Impel Down' },
  { from: 457, to: 489, location: 'Marineford' },
  { from: 523, to: 574, location: 'Île des Hommes-Poissons' },
  { from: 579, to: 625, location: 'Punk Hazard' },
  { from: 629, to: 746, location: 'Dressrosa' },
  { from: 751, to: 779, location: 'Zou' },
  { from: 783, to: 877, location: 'Whole Cake Island' },
  { from: 890, to: 1088, location: 'Wano' },
];

function App() {
  const [image, setImage] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [options, setOptions] = useState([]);
  const [result, setResult] = useState('');

  useEffect(() => {
    fetchRandomEpisode();
  }, []);

  const getLocationByEpisode = (episodeNumber) => {
    const arc = ARC_LOCATIONS.find(a => episodeNumber >= a.from && episodeNumber <= a.to);
    return arc ? arc.location : 'Lieu inconnu';
  };

  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  const fetchRandomEpisode = async () => {
    let episodeNumber;
    let data;
    let attempts = 0;

    while (!data && attempts < 10) {
      attempts++;
      episodeNumber = Math.floor(Math.random() * (960 - 1 + 1)) + 1;
      try {
        const res = await fetch(`https://api.jikan.moe/v4/anime/21/episodes/${episodeNumber}`);
        if (res.status === 429) {
          console.warn('Trop de requêtes. Pause plus longue...');
          await sleep(3000);
          continue;
        }
        const json = await res.json();
        if (res.ok && json.data && json.data.images?.jpg?.image_url) {
          data = json.data;
        }
      } catch (err) {
        console.warn(`Épisode ${episodeNumber} indisponible, tentative suivante...`);
      }
      await sleep(1000);
    }

    if (!data) {
      setResult("⚠️ Impossible de charger un épisode pour le moment. Réessaie dans quelques instants.");
      return;
    }

    const imageUrl = data.images.jpg.image_url;
    const location = getLocationByEpisode(episodeNumber);

    setCorrectAnswer(location);
    setImage(imageUrl);

    const shuffled = [
      location,
      ...ARC_LOCATIONS.filter(loc => loc.location !== location)
        .sort(() => 0.5 - Math.random())
        .slice(0, 3)
        .map(loc => loc.location),
    ].sort(() => 0.5 - Math.random());

    setOptions(shuffled);
    setResult('');
  };

  const handleAnswer = (choice) => {
    if (choice === correctAnswer) {
      setResult('🏴‍☠️ Correct ! You’re a true navigator.');
    } else {
      setResult(`❌ Nope ! Zoro aurait choisi "${choice}" aussi... La bonne réponse était ${correctAnswer}.`);
    }
    setTimeout(fetchRandomEpisode, 2500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-900 to-indigo-900 text-white p-6 text-center">
      <h1 className="text-4xl font-bold mb-4">🌍 OneGuessr: Grand Line Edition</h1>
      {image ? (
        <div>
          <img src={image} alt="One Piece scene" className="mx-auto mb-4 rounded-lg shadow-lg max-h-[400px]" />
          <div className="flex flex-wrap justify-center gap-4">
            {options.map((opt, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(opt)}
                className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-lg shadow"
              >
                {opt}
              </button>
            ))}
          </div>
          <div className="mt-6 text-lg">{result}</div>
        </div>
      ) : (
        <p>Chargement de l’image… 🌀</p>
      )}
    </div>
  );
  useEffect(() => {
    fetch("/onepiece_dataset.json")
      .then((res) => res.json())
      .then((data) => {
        setDataset(data);
        pickRandomImage(data);
      });
  }, []);

  const [dataset, setDataset] = useState([]);

  const pickRandomImage = (data) => {
    const randomEntry = data[Math.floor(Math.random() * data.length)];
    const image = randomEntry.images[Math.floor(Math.random() * randomEntry.images.length)];
    const chapter = randomEntry.chapter;

    const location = getLocationByChapter(chapter);
    setCorrectAnswer(location);
    setImage(image);

    const shuffled = [
      location,
      ...ARC_LOCATIONS.filter(loc => loc.location !== location)
        .sort(() => 0.5 - Math.random())
        .slice(0, 3)
        .map(loc => loc.location),
    ].sort(() => 0.5 - Math.random());

    setOptions(shuffled);
    setResult('');
  };
  const getLocationByChapter = (chapter) => {
    const arc = ARC_LOCATIONS.find(a => chapter >= a.from && chapter <= a.to);
    return arc ? arc.location : "Lieu inconnu";
  };
}

export default App;
