import React, { useEffect, useState } from 'react';
import '../App.css';

const ARC_LOCATIONS = [
//   { from: 1, to: 3, location: 'East Blue' },
//   { from: 4, to: 8, location: "Ville d'Orange" },
//   { from: 9, to: 18, location: 'Village de Sirop' },
//   { from: 19, to: 30, location: 'Baratie' },
//   { from: 31, to: 45, location: 'Arlong Park' },
//   { from: 48, to: 53, location: 'Loguetown' },
//   { from: 61, to: 63, location: 'Reverse Mountain' },
//   { from: 64, to: 67, location: 'Whiskey Peak' },
//   { from: 70, to: 78, location: 'Little Garden' },
//   { from: 79, to: 91, location: 'Royaume de Drum' },
//   { from: 92, to: 130, location: 'Alabasta' },
//   { from: 153, to: 195, location: 'Skypiea' },
//   { from: 229, to: 263, location: 'Water 7' },
//   { from: 264, to: 312, location: 'Enies Lobby' },
//   { from: 337, to: 381, location: 'Thriller Bark' },
//   { from: 385, to: 407, location: 'Archipel Sabaody' },
//   { from: 408, to: 417, location: 'Amazon Lily' },
//   { from: 422, to: 452, location: 'Impel Down' },
//   { from: 457, to: 489, location: 'Marineford' },
//   { from: 523, to: 574, location: 'ÃŽle des Hommes-Poissons' },
  { from: 579, to: 625, location: 'Punk Hazard' },
  { from: 629, to: 746, location: 'Dressrosa' },
  { from: 751, to: 779, location: 'Zou' },
  { from: 783, to: 877, location: 'Whole Cake Island' },
  { from: 890, to: 1088, location: 'Wano' },
  { from: 1058, to: 1125, location: 'Egg Head' },
  { from: 1126, to: 1144, location: 'Erbaf' }
];

function GamePage() {

  const [image, setImage] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [options, setOptions] = useState([]);
  const [result, setResult] = useState('');
  const [dataset, setDataset] = useState([]);
  const [timer, setTimer] = useState(10);
  const [gameOver, setGameOver] = useState(false);
  const [answered, setAnswered] = useState(false); 
  const [errorCount, setErrorCount] = useState(0); 
  const [score, setScore] = useState(0); 
  const [bestScore, setBestScore] = useState(localStorage.getItem("bestScore") || 0); // Best score from localStorage

  useEffect(() => {
    fetch("/onepiece_dataset.json")
      .then((res) => res.json())
      .then((data) => {
        setDataset(data);
        pickRandomImage(data);
      });
  }, []);

  useEffect(() => {
    if (timer === 0) {
      
      setErrorCount(prev => prev + 1); 
      setResult('â±ï¸ Time is up! You lost! Starting a new game...');
      
      if (errorCount >= 3) {
        setGameOver(true); // Game over if we reach 3 errors (or losses)
        return;
      }

      // Set a timeout for a new round
      setTimeout(() => {
        setAnswered(false);
        setTimer(10);
        pickRandomImage(dataset);
      }, 1000);
    }
    if (!gameOver && !answered) {
      const interval = setInterval(() => {
        setTimer(prev => Math.max(prev - 1, 0));
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [timer, gameOver, answered]);

  const getLocationByChapter = (chapter) => {
    const arc = ARC_LOCATIONS.find(a => chapter >= a.from && chapter <= a.to);
    return arc ? arc.location : 'Lieu inconnu';
  };

  const pickRandomImage = (data) => {
    const randomEntry = data[Math.floor(Math.random() * data.length)];
    const image = randomEntry.images[Math.floor(Math.random() * randomEntry.images.length)]
      .replace("https://lelscans.nethttps://lelscans.net", "https://lelscans.net");
    const chapter = randomEntry.chapter;

    const location = getLocationByChapter(chapter);
    setCorrectAnswer(location);
    setImage(image);

    const filteredArcs = ARC_LOCATIONS.filter(loc => loc.from >= 640 && loc.location !== location);
    const shuffled = [
      location,
      ...filteredArcs
        .sort(() => 0.5 - Math.random())
        .slice(0, 4) // 4 choices
        .map(loc => loc.location),
    ].sort(() => 0.5 - Math.random());

    setOptions(shuffled);
    setResult('');
    setTimer(10);  // Reset timer
    setAnswered(false);  // Reset answered state when new image is picked
  };

  const handleAnswer = (choice) => {
    const isCorrect = choice === correctAnswer;
  
    // Enregistrer la rÃ©ponse dans l'historique
    const history = JSON.parse(localStorage.getItem("answerHistory") || "[]");
    history.push({
      correctArc: correctAnswer,
      userAnswer: choice,
      isCorrect: isCorrect,
      timestamp: Date.now(),
    });
    localStorage.setItem("answerHistory", JSON.stringify(history));
  
    // Afficher le message de rÃ©sultat
    if (isCorrect) {
      setScore(prev => prev + 1);
      setResult('Nice job ðŸ—¿ðŸ—¿');
    } else {
      setErrorCount(prev => prev + 1);
      setResult(`THIS IS NOT THIS ARC DUMB ASS ðŸ«µðŸ¤£ðŸ¤£... La bonne rÃ©ponse Ã©tait ${correctAnswer}.`);
  
      if (errorCount + 1 >= 3) {
        setGameOver(true);
        setResult('Game Over! You reached 3 wrong answers.');
        return;
      }
    }
  
    // Mettre Ã  jour le meilleur score
    if (score > bestScore) {
      setBestScore(score);
      localStorage.setItem("bestScore", score);
    }
  
    setAnswered(true);
  
    // PrÃ©parer la prochaine image aprÃ¨s un court dÃ©lai
    setTimeout(() => {
      setAnswered(false);
      setTimer(10);
      pickRandomImage(dataset);
    }, 1000);
  };

  const handleReplay = () => {
    setScore(0);  // Reset the score
    setErrorCount(0);  // Reset the error count
    setGameOver(false);  // Reset the game over state
    setResult('');  // Clear the result message
    pickRandomImage(dataset);  // Start a new game
  };

  // Array of chapeaux to show based on error count
  const chapeaux = [
    errorCount < 1 && "/Mugiwara.png",  // Utilisation de l'image "Chapeau.jpg" Ã  partir du dossier public
    errorCount < 2 && "/Mugiwara.png",
    errorCount < 3 && "/Mugiwara.png"
  ];

  return (
    <div className="min-h-screen bg-black text-white p-6 text-center flex flex-col items-center">
      {/* Display score */}
      <div className="absolute top-4 left-4 text-lg font-bold" style={{ color: '#E6B96F' }}>
        Score: {score}
      </div>
      <div className="absolute top-4 right-4 text-lg font-bold" style={{ color: '#E6B96F' }}>
        Best Score: {bestScore}
      </div>

      <div className="flex justify-center mb-4 mt-6">
        <img
          src="/Luffy.jpg"
          alt="Luffy"
          className="w-40 h-40 object-cover rounded-full border-4"
          style={{ borderColor: '#CC213B' }}
        />
      </div>
      <h1 className="text-4xl font-bold mb-4" style={{ color: '#E6B96F' }}>
        OneGuessr
      </h1>

      {/* Display lives (chapeaux) */}
      <div className="flex justify-center mb-4 mt-2"> {/* PositionnÃ© juste aprÃ¨s le titre */}
        {chapeaux.map((chapeau, index) => chapeau && (
          <img 
            key={index} 
            src={chapeau} 
            alt={`Chapeau ${index + 1}`} 
            className="w-12 h-12 mx-2 object-cover"
          />
        ))}
      </div>

      {/* Timer section */}
      {!answered && (
        <div className="relative mb-4 w-full max-w-[500px] mx-auto">
          <div
            className="h-2 bg-[#E6B96F] rounded-full"
            style={{ width: `${(timer / 10) * 100}%`, transition: 'width 1s linear' }}
          />
          <p className="absolute top-0 left-0 right-0 bottom-0 flex justify-center items-center text-lg font-bold">
            {timer} s
          </p>
        </div>
      )}

      {gameOver ? (
        <div className="mt-6 text-lg" style={{ color: '#E6B96F' }}>
          {result}
          <div className="mt-4">
            <button 
              onClick={handleReplay} 
              className="bg-[#CC213B] text-white font-bold py-2 px-4 rounded-lg shadow"
            >
              Rejouer
            </button>
          </div>
        </div>
      ) : (
        <>
          {/* Show image and options if not answered */}
          {!answered && image && (
            <>
              <img
                src={image}
                alt="One Piece scene"
                className="mx-auto mb-4 rounded-lg shadow-lg max-h-[400px] border-4"
                style={{ borderColor: '#CC213B' }}
              />
              <div className="grid grid-cols-2 gap-4 mb-4">
                {options.slice(0, 4).map((opt, idx) => (
                  <button
                    key={idx}
                    onClick={() => handleAnswer(opt)}
                    className="text-black font-bold py-2 px-4 rounded-lg shadow"
                    style={{ backgroundColor: '#E6B96F' }}
                  >
                    {opt}
                  </button>
                ))}
              </div>
            </>
          )}
          <div className="mt-6 text-lg" style={{ color: '#E6B96F' }}>{result}</div>
        </>
      )}
    </div>
  );
}

export default GamePage;
