import React, { useEffect, useState } from 'react';
import './App.css';

const LOCATIONS = [
  { name: 'Alabasta' },
  { name: 'Skypiea' },
  { name: 'Water 7' },
  { name: 'Dressrosa' },
  { name: 'Whole Cake Island' },
  { name: 'Wano' },
];

function App() {
  const [image, setImage] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [options, setOptions] = useState([]);
  const [result, setResult] = useState('');

  useEffect(() => {
    fetchRandomLocation();
  }, []);

  const fetchRandomLocation = async () => {
    const randomLoc = LOCATIONS[Math.floor(Math.random() * LOCATIONS.length)];
    setCorrectAnswer(randomLoc.name);

    try {
      const res = await fetch('https://api.jikan.moe/v4/anime/21/pictures');
      const data = await res.json();
      const randomImg = data.data[Math.floor(Math.random() * data.data.length)].jpg.image_url;
      setImage(randomImg);
    } catch (err) {
      console.error('Image fetch failed:', err);
      setImage(null);
    }

    const shuffled = [
      randomLoc.name,
      ...LOCATIONS.filter(loc => loc.name !== randomLoc.name)
        .sort(() => 0.5 - Math.random())
        .slice(0, 3),
    ].sort(() => 0.5 - Math.random());

    setOptions(shuffled);
    setResult('');
  };

  const handleAnswer = (choice) => {
    if (choice === correctAnswer) {
      setResult('üè¥‚Äç‚ò†Ô∏è Correct ! You‚Äôre a true navigator.');
    } else {
      setResult(`‚ùå Nope ! Zoro would‚Äôve chosen "${choice}" too... It was ${correctAnswer}.`);
    }
    setTimeout(fetchRandomLocation, 2500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-900 to-indigo-900 text-white p-6 text-center">
      <h1 className="text-4xl font-bold mb-4">üåç OneGuessr: Grand Line Edition</h1>
      {image ? (
        <div>
          <img src={image} alt="One Piece scene" className="mx-auto mb-4 rounded-lg shadow-lg max-h-[400px]" />
          <div className="flex flex-wrap justify-center gap-4">
            {options.map((opt, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(opt)}
                className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-lg shadow"
              >
                {opt}
              </button>
            ))}
          </div>
          <div className="mt-6 text-lg">{result}</div>
        </div>
      ) : (
        <p>Chargement de l‚Äôimage‚Ä¶ üåÄ</p>
      )}
    </div>
  );
}

export default App;
